CREATE MASTER KEY
ENCRYPTION BY PASSWORD='Hello@14.';

CREATE DATABASE SCOPED CREDENTIAL aw_credential
WITH
    IDENTITY = 'Managed Identity';

CREATE EXTERNAL DATA SOURCE aw_source_gold
WITH (
    LOCATION = 'https://awadls5.dfs.core.windows.net/gold/',
    CREDENTIAL = aw_credential
);

CREATE EXTERNAL FILE FORMAT aw_format_parquet
WITH (
    FORMAT_TYPE = PARQUET,
    DATA_COMPRESSION = 'org.apache.hadoop.io.compress.SnappyCodec'
);

CREATE EXTERNAL TABLE gold.ext_sales
WITH (
    LOCATION = 'ext_sales',
    DATA_SOURCE = aw_source_gold,
    FILE_FORMAT = aw_format_parquet
)
AS
    SELECT * FROM gold.vw_sales;
